import React, { useState } from 'react'
import { useColorMode, Heading, Text, Flex, Stack } from '@chakra-ui/core'
import quoteData from '../data/quotes'

import { secondaryTextColor } from '../styles/colors'

const Index = () => {
  const { colorMode } = useColorMode()
  const [quoteIndex, setQuoteIndex] = useState(
    Math.floor(Math.random() * quoteData.length),
  )

  return (
    <>
      <h1>Fonzie Meatballs</h1>
      <div>
        I write essays on things I want to learn more about. Right now that's
        how to build and grow companies. In the future, I hope to write about
        investing, programming, and more. Follow me on Twitter for threads that
        summarize them.
      </div>
    </>
  )
}

export default Index
